import React from "react";

import { parse, Transaction } from "ethers/utils/transaction";

interface HexDecoderState {
    decodeTx: string;
    decodedTx?: Transaction;
    errorMsg: string;
}

class HexDecoder extends React.Component<{}, HexDecoderState> {
    constructor(props) {
        super(props);
        this.state = {
            decodeTx: "",
            errorMsg: "",
        };
    }

    decodeTx = (e: React.ChangeEvent<HTMLTextAreaElement>): void => {
        try {
            let decodedTx = parse(e.target.value);
            if (decodedTx) {
                this.setState({
                    decodedTx,
                });
            }
            console.log(decodedTx)
        } catch (e) {
            if (e instanceof Error) {
                console.log(e.message);
                this.setState({
                    errorMsg: e.message
                })
            }
        }
    }

    render = () => (
        <div>
            <div className="form-group">
                <label html-for="decode-tx">Enter Raw Hex</label>
                <textarea className="form-control" id="decode-tx" onChange={this.decodeTx} html-rows="4"></textarea>
                {this.state.errorMsg && (
                    <div class="invalid-feedback">
          Please choose a username.
        </div>
                )}
            </div>
            {JSON.stringify(this.state.decodedTx)}
        </div>
    )
}

export default HexDecoder;
