import tx, { Transaction } from "ethers/utils/transaction";
import { hexDataLength } from "ethers/utils/bytes";

let currentTx: Transaction;

function decodeTx(raw_tx: string): void {
  let parsedTx = tx.parse(raw_tx);
  if (parsedTx) {
      currentTx = parsedTx;
  }
}

const HexDecoder = () => (
    <>
        <div>
            <p>Enter String:</p>
            <input onChange={decodeTx} />
            {currentTx}
        </div>
    </>
);

export default hexDecoder
