import { parse, Transaction } from "ethers/utils/transaction";
import { hexDataLength } from "ethers/utils/bytes";

let currentTx: Transaction;

function decodeTx(e: InputEvent): void {
  let parsedTx = parse(e.target);
  if (parsedTx) {
      currentTx = parsedTx;
  }
}

const HexDecoder = () => (
    <>
        <div>
            <p>Enter String:</p>
            <input onChange={decodeTx} />
            {currentTx}
        </div>
    </>
);

export default HexDecoder;
