import React from "react";
import { Transaction, populateTransaction } from "ethers/utils/transaction";

    // hash?: string;
    // to?: string;
    // from?: string;
    // nonce: number;
    // gasLimit: BigNumber;
    // gasPrice: BigNumber;
    // data: string;
    // value: BigNumber;
    // chainId: number;
    // r?: string;
    // s?: string;
    // v?: number;
const TxInfo: React.FC<{
    t: Transaction;
}> = ({t}) => (
    <>
        <div>
            {Object.keys(t).forEach(k => (
<IfExists t={t} k="hash" type="string" key="i" />
))}
        </div>
    </>
);

interface IfExistsProps {
    t: Transaction;
    k: string;
    type: string;
}

const IfExists: React.FC<IfExistsProps> = ({t, k, type}) => {
    if (!t[k]) {
        return null;
    }

    switch (type) {
        case "string":
            return <div>{k}: {t[k]}</div>
    }
}

