import React from "react";

import { parse, Transaction } from "ethers/utils/transaction";
import { hexDataLength } from "ethers/utils/bytes";

interface HexDecoderState {
    decodeTx: string;
    decodedTx?: Transaction;
}

class HexDecoder extends React.Component<{}, HexDecoderState> {
    constructor(props) {
        super(props);
        this.state = {
            decodeTx: ""
        };
    }

    decodeTx = (e: React.ChangeEvent<HTMLInputElement>): void => {
        let decodedTx = parse(e.target.value);
        if (decodedTx) {
            this.setState({
                decodedTx,
            });
        }
        console.log(decodedTx)
    }

    render = () => (
        <div>
            <p>Enter String:</p>
            <input type="text" onChange={this.decodeTx} />
            <style jsx>{`
            `}</style>
            {JSON.stringify(this.state.decodedTx)}
        </div>
    )
}

export default HexDecoder;
