import * as React from "react";

import { parse, Transaction } from "ethers/utils/transaction";
import { hexDataLength } from "ethers/utils/bytes";

let currentTx: Transaction;

function decodeTx(e: React.ChangeEvent<HTMLInputElement>): void {
  let parsedTx = parse(e.target.value);
  if (parsedTx) {
      currentTx = parsedTx;
  }
  console.log(parsedTx)
}

const HexDecoder:React.Component = () => (
    <>
        <div>
            <p>Enter String:</p>
            <input type="text" onChange={decodeTx} />
            {currentTx}
        </div>
    </>
);

interface HexDecoderState {
    decodeTx: string;
    decodedTx?: Transaction;
}

class Clock extends React.Component<{}, HexDecoderState> {
  constructor(props) {
    super(props);
    this.state = {
        decodeTx: "" 
    };
  }

  render() {
  }
}

export default HexDecoder;
