import React from "react";
import { Transaction, populateTransaction } from "ethers/utils/transaction";
import { BigNumber } from "ethers/utils/bignumber";

const keyType = {
    hash: "string",
    to: "string",
    from: "string",
    nonce: "number",
    gasLimit: "BigNumber",
    gasPrice: "BigNumber",
    data: "string",
    value: "BigNumber",
    chainId: "number",
    r: "string",
    s: "string",
    v: "number"
};

const TxInfo: React.FC<{
    t: Transaction;
}> = ({ t }) => (
    <>
        <div>
            {Object.keys(t).map((k, i) => (
                <IfExists t={t} k={k} type={keyType[k]} key={i} />
            ))}
        </div>
    </>
);

interface IfExistsProps {
    t: Transaction;
    k: string;
    type: string;
}

const IfExists: React.FC<IfExistsProps> = ({ t, k, type }) => {
    if (!t[k]) {
        return null;
    }

    if (t[k] )

    switch (type) {
        case "string":
        case "number":
            return (
                <div>
                    {k}: {t[k]}
                </div>
            );
        case "BigNumber":
            (t[k] as BigNumber).toString()
    }
};

