// gasPrice: BigNumber;
// data: string;
// value: BigNumber;
// chainId: number;
// r?: string;
// s?: string;
// v?: number;

const keyType = {

}

const TxInfo: React.FC<{
    t: Transaction;
}> = ({ t }) => (
    <>
        <div>
            {Object.keys(t).map((k, i) => (
                <IfExists t={t} k={k} type={keyType[t]} key={i} />
            ))}
        </div>
    </>
);

interface IfExistsProps {
    t: Transaction;
    k: string;
    type: string;
}

const IfExists: React.FC<IfExistsProps> = ({ t, k, type }) => {
    if (!t[k]) {
        return null;
    }

    switch (type) {
        case "string":
            return (
                <div>
                    {k}: {t[k]}
                </div>
            );
    }
};

